import React, { useState } from 'react';

function Content() {
  const [inputText, setInputText] = useState('');
  const [errorMessages, setErrorMessages] = useState([]);

  const checkError = (e) => {
    e.preventDefault()
    const lines = inputText.split('\n');
    const errors = [];

    lines.forEach((line, index) => {
      const parts = line.trim().split(/[,:= ]+/); // Split by colons, commas, spaces, or equal signs
      if (parts.length !== 2) {
        errors.push(`Line ${index + 1} does not have exactly two parts.`);
      } else {
        const [address, value] = parts;
        if (!isValidEthereumAddress(address)) {
          errors.push(`Line ${index + 1} wrong ammount`);
        }
        if (!/^\d+$/.test(value)) {
          errors.push(`Line ${index + 1} wrong ammount`);
        }
      }
    });

    setErrorMessages(errors);
  };

  // Function to validate Ethereum address (basic check for hexadecimal string)
  const isValidEthereumAddress = (address) => {
    const ethereumAddressRegex = /^(0x)?[0-9a-fA-F]{40}$/;
    return ethereumAddressRegex.test(address);
  };

  return (
    <div className="container py-2">
      <form>
        <div className="mb-3">
          <label htmlFor="exampleFormControlTextarea1" className="form-label">Addresses with Amounts</label>
          <textarea
            className="form-control bg-light text-emphasis-dark"
            rows="9"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
          ></textarea>
        </div>

        <div className='mb-3'>
          <p>Separated by ',' or ' ' or '='</p>
        </div>



        <div>
          {errorMessages.length > 0 && (
            <div className='d-flex border border-danger rounded-2'>
            <div className='p-2'>
            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="red" className="bi bi-exclamation-circle" viewBox="0 0 16 16">
      <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z"/>
      <path d="M7.002 11a1 1 0 1 1 2 0 1 1 0 0 1-2 0zM7.1 4.995a.905.905 0 1 1 1.8 0l-.35 3.507a.552.552 0 0 1-1.1 0L7.1 4.995z"/>
    </svg>
            </div>
            <div className='text-danger p-1'>
            <ul>
                    {errorMessages.map((errorMessage, index) => (
                      <li key={index}>{errorMessage}</li>
                    ))}
                  </ul>
            </div>
            </div>
          )}
          <div className="d-grid gap-2">
          <button className='btn btn-primary' onClick={checkError}>Next</button>
        </div>
        </div>
      </form>
    </div>
  )
}

export default Content;
