import React, { useState } from 'react';
import Modal from 'react-modal';

function Content() {
  const [inputText, setInputText] = useState('');
  const [errorMessages, setErrorMessages] = useState([]);
  const [duplicates, setDuplicates] = useState([]);
  const [mergedInput, setMergedInput] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);

  const checkError = (e) => {
    e.preventDefault();

    const lines = inputText.split('\n');
    const errors = [];
    const duplicateAddresses = {};

    lines.forEach((line, index) => {
      const parts = line.trim().split(/[,:= ]+/);
      if (parts.length !== 2) {
        errors.push(`Line ${index + 1} does not have exactly two parts.`);
      } else {
        const [address, value] = parts;
        if (!isValidEthereumAddress(address)) {
          errors.push(`Line ${index + 1} contains an invalid Ethereum address.`);
        }
        if (!/^\d+$/.test(value)) {
          errors.push(`Line ${index + 1} contains a non-numeric value.`);
        }
        if (duplicateAddresses[address]) {
          duplicateAddresses[address].push(index);
        } else {
          duplicateAddresses[address] = [index];
        }
      }
    });

    const duplicatesFound = Object.values(duplicateAddresses).filter((indexes) => indexes.length > 1);

    setDuplicates(duplicatesFound);
    setErrorMessages(errors);
  };

  const mergeDuplicates = () => {
    const linesToMerge = inputText.split('\n'); // Define linesToMerge here
    const mergedValuesMap = {};

    linesToMerge.forEach((line) => {
      const parts = line.trim().split(/[,:= ]+/);
      const [address, value] = parts;

      if (mergedValuesMap[address]) {
        mergedValuesMap[address] += parseInt(value, 10);
      } else {
        mergedValuesMap[address] = parseInt(value, 10);
      }
    });

    const mergedInputLines = Object.entries(mergedValuesMap).map(([address, value]) => {
      return `${address} ${value}`;
    });

    const mergedInputText = mergedInputLines.join('\n');
    setMergedInput(mergedInputText);
  };

  const handleMergeOption = (shouldMerge) => {
    if (shouldMerge) {
      mergeDuplicates();
    } else {
      // If the user chooses not to merge, keep the first occurrence
      // Set the merged input to the original input
      setMergedInput(inputText);
    }
    setIsModalOpen(false);
  };

  const openModal = (index) => {
    setSelectedDuplicate(index);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setSelectedDuplicate(null);
    setIsModalOpen(false);
  };

  const isValidEthereumAddress = (address) => {
    const ethereumAddressRegex = /^(0x)?[0-9a-fA-F]{40}$/;
    return ethereumAddressRegex.test(address);
  };

  return (
    <div className="container py-2">
      <form>
        <div className="mb-3">
          <label htmlFor="exampleFormControlTextarea1" className="form-label">Addresses with Amounts</label>
          <textarea
            className="form-control bg-light text-emphasis-dark"
            rows="9"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
          ></textarea>
        </div>

        <div className='mb-3'>
          <p>Separated by ',' or ' ' or '='</p>
        </div>

        <div className="d-grid gap-2">
          <button type="button" className='btn btn-primary' onClick={checkError}>Check Errors</button>
          <button type="button" className='btn btn-success' onClick={mergeDuplicates}>Merge Duplicates</button>
        </div>

        <div>
          {errorMessages.length > 0 && (
            <div>
              <p>Errors found:</p>
              <ul>
                {errorMessages.map((errorMessage, index) => (
                  <li key={index}>{errorMessage}</li>
                ))}
              </ul>
            </div>
          )}
        </div>

        {duplicates.length > 0 && (
          <div>
            <p>Duplicate addresses found:</p>
            <ul>
              {duplicates.map((indexes, index) => (
                <li key={index}>
                  Address: {lines[indexes[0]]} - Duplicates: {indexes.map((i) => linesToMerge[i]).join(', ')}
                  <button onClick={() => openModal(index)}>Merge</button>
                </li>
              ))}
            </ul>
          </div>
        )}

        {mergedInput && (
          <div>
            <p>Merged Input:</p>
            <textarea
              className="form-control bg-light text-emphasis-dark"
              rows="9"
              value={mergedInput}
              readOnly
            ></textarea>
          </div>
        )}

        <Modal isOpen={isModalOpen} onRequestClose={closeModal}>
          <h2>Duplicate Address Found</h2>
          <p>Do you want to merge the duplicates or keep the first occurrence?</p>
          <button onClick={() => handleMergeOption(true)}>Merge</button>
          <button onClick={() => handleMergeOption(false)}>Keep First Occurrence</button>
        </Modal>
      </form>
    </div>
  );
}

export default Content;
